import {
  GET_TOP_BANNER, GET_HOT_RECOMMEND, GET_NEW_ALBUM, 
  GET_UP_TOPLIST, GET_NEW_TOPLIST, GET_ORIGIN_TOPLIST, GET_SETTLE_SINGER
} from './constants'

import {Map} from 'immutable'

const initState = Map({
  topBanner: [],
  hotRecommend: [],
  newAlbum: [],
  toplist_up: {},
  toplist_new: {},
  toplist_origin: {},
  settleSinger: []
})

export default function recommendReducer(preState = initState, action) {
  const {type, data} = action
  switch(type) {
    case GET_TOP_BANNER:
      return preState.set('topBanner', data)
    case GET_HOT_RECOMMEND:
      return preState.set('hotRecommend', data)
    case GET_NEW_ALBUM:
      return preState.set('newAlbum', data)
    case GET_UP_TOPLIST:
      return preState.set('toplist_up', data)
    case GET_NEW_TOPLIST:
      return preState.set('toplist_new', data)
    case GET_ORIGIN_TOPLIST:
      return preState.set('toplist_origin', data)
    case GET_SETTLE_SINGER:
      return preState.set('settleSinger', data)
    default:
      return preState
  }
}
