import styled from 'styled-components'

export const PlaylistItemWrapper = styled.div`
  display: flex;
  align-items: center;
  width: 552px;
  height: 28px;
  padding-left: 30px;
  margin-top: 2px;
  cursor: pointer;
  color: #ccc;

  &:hover {
    color: #fff;
    background-color: rgba(0, 0, 0, 0.4);
  }

  .song-name {
    width: 246px;
    height: 28px;
    line-height: 28px;
  }

  .control {
    display: flex;
    width: 108px;
    padding-left: 10px;

    .anticon-delete,
    .anticon-download,
    .anticon-folder-add {
      opacity: 0;
      color: #ccc;
      font-size: 14px;
      margin: 2px 6px 0;
    }

    .anticon-delete:hover,
    .anticon-download:hover,
    span:hover,
    .anticon-folder-add:hover {
      color: #fff;
    }
  }

  &:hover .anticon-delete,
  &:hover .anticon-download,
  &:hover .anticon-folder-add {
    opacity: 1;
  }

  .singer {
    width: 80px;
    padding-left: 10px;
  }

  .duration {
    width: 45px;
    padding-left: 10px;
    margin-left: 10px;
    color: #666;
  }

`
