import React, { memo, useEffect, useState, useRef, useCallback } from 'react';
import { shallowEqual, useDispatch, useSelector } from 'react-redux';
import {addToPlaylistAsync} from './redux/action'

import { NavLink } from 'react-router-dom';
import {getAudioSrc} from '@/utils/format-data'

import {PlayerbarWrapper} from './style'
import LeftControl from './child-components/leftControl';
import MidContent from './child-components/middleContent';
import RightControl from './child-components/rightControl';


export default memo(function PlayerBar() {

  // 是否正在播放
  const [isPlaying, setIsPlaying] = useState(false)
  // 歌曲播放时间
  const [playTime, setPlayTime] = useState(0)
  // Slider进度条的值
  const [sliderValue, setSliderValue] = useState(0)
  // 让audio中的onTimeUpdate事件 与 拖动进度条 互斥(不能同时更改进度与播放时间，不然会有bug)
  // flag为true表示正在拖动滑块改变进度，此时audio不应该再更改进度条(播放时间与进度值)
  const [flag, setFlag] = useState(false)


  // console.log('playbar', isPlaying);

  const dispatch = useDispatch()
  useEffect(() => {
    dispatch(addToPlaylistAsync('185726'))
    audioRef.current.src = getAudioSrc('185726')
  }, [dispatch])

  const audioRef = useRef()

  // 当前播放歌曲
  const {playlist, currentIndex} = useSelector(state => ({
    playlist: state.getIn(['player', 'playlist']),
    currentIndex: state.getIn(['player', 'currentIndex']),
  }), shallowEqual)
  const currentSong = playlist && playlist[currentIndex]

  // 利用audio中的onTimeUpdate更新歌曲的播放时间
  const updatePlayTime = (e) => {
    const currentTime = e.target.currentTime
    if(!flag) {
      setPlayTime(currentTime * 1000)
      setSliderValue(((currentTime * 1000) / currentSong.dt) * 100);
    }
  }

  return (
    <PlayerbarWrapper className="sprite_player">
      <div className="w980 content">
        <LeftControl musicdom={audioRef} isPlaying={isPlaying} setIsPlaying={setIsPlaying}></LeftControl>
        <MidContent musicdom={audioRef} playTime={playTime} setPlayTime={setPlayTime} 
          sliderValue={sliderValue} setSliderValue={setSliderValue}
          setFlag={setFlag}></MidContent>
        <RightControl></RightControl>
      </div>
      <audio ref={audioRef} id="audio" onTimeUpdate={updatePlayTime} />
    </PlayerbarWrapper>
  );
});
