import React, {memo, useCallback} from 'react'
import {LeftWrapper} from './style'

export default memo(function LeftControl(props) {

  const {musicdom, isPlaying, setIsPlaying} = props

  musicdom.current.src = "https://music.163.com/song/media/outer/url?id=185726.mp3"

  console.log(musicdom.current);

  const playMusic = useCallback(() => {
    // 设置src属性
    setIsPlaying(!isPlaying);
    // 播放音乐
    isPlaying ? musicdom.current.pause() : musicdom.current.play();
  }, [isPlaying]);

  return (
    <LeftWrapper>
      <button className="sprite_player pre"></button>
      <button className="sprite_player play" onClick={playMusic}></button>
      <button className="sprite_player next"></button>
    </LeftWrapper>
  )
})
