import React, { memo } from 'react'

import { hotDjradio } from '@/common/local-data.js'
import { HotDjradioWrapper } from './style'

export default memo(function HotDjradio() {
  return (
    <HotDjradioWrapper>
      <div className="header">
        <h3 className="settle-singer">热门主播</h3>
      </div>
      <ul className="artist-container">
        {hotDjradio.map(item => {
          return (
            <li key={item.picUrl} className="content">
              <div className="artist-image">
                <img src={getSizeImage(item.picUrl, 40)} alt="" />
              </div>
              <div className="artist-info">
                <a href={item.url} className="artist-name">{item.name}</a>
                <a href={item.url} className="artist-detail text-nowrap">{item.position}</a>
              </div>
            </li>
          )
        })}
      </ul>
    </HotDjradioWrapper>
  )
})
