import React, {memo} from 'react'
import {useDispatch, useSelector, shallowEqual} from 'react-redux'

import {DownloadOutlined, UndoOutlined} from '@ant-design/icons'
import {Tooltip, Slider} from 'antd'
import {RightWrapper} from './style'

export default memo(function RightControl(props) {

  const {musicdom, showVolume, setShowVolume} = props

  // 展示音量条
  const handleVolume = (e) => {
    e.preventDefault()
    setShowVolume(!showVolume)
  }

  // 调整音量
  const changeVolume = (value) => {
    musicdom.current.volume = value / 100
  }


  return (
    <RightWrapper>
      <div className="left">
        <a href="#" target="_blank" rel="noopener noreferrer" title="下载音乐" className="l-control download">
          <DownloadOutlined />
        </a>
        <a href="#" title="重新播放" className="l-control refresh"><UndoOutlined /></a>
      </div>
      <div className="right sprite_player">
        <a href="#" title="音量" className="sprite_player r-control volume" onClick={handleVolume}></a>
        <a href="#" title="顺序播放" className="sprite_player r-control loop"></a>
        <a href="#" title="播放列表" className="sprite_player r-control playlist">{2}</a>
      </div>
      {/* Slide 音量条 */}
      <div className="sprite_player top-volume" style={{ display: showVolume ? 'block' : 'none'}}>
        <Slider vertical defaultValue={30} />
      </div>
    </RightWrapper>
  )
})
