import {HeaderWrapper, HeaderLeft, HeaderRight} from './style'
import {headerLinks} from '@/common/local-data'
import {NavLink} from 'react-router-dom'

export default function AppHeader() {

  const handleHeaderLinks = (linkObj, index) => {
    if(index < 3){
      return (
        <NavLink key={linkObj.title} to={linkObj.link} className="header-item" activeClassName="active-link">
          <em>{linkObj.title}</em>
          <i></i>
        </NavLink>
      )
    } else{
      return (
        <a key={linkObj.title} href={linkObj.link}>{linkObj.title}</a>
      )
    }
  }


  return (
    <HeaderWrapper>
      <HeaderLeft>
        <h1>
          <a href="http://localhost:3000">
            网易云音乐
          </a>
        </h1>
        <div>
          {
            headerLinks.map((linkObj, index) => {
              return handleHeaderLinks(linkObj, index);
            })
          }
        </div>
      </HeaderLeft>
      <HeaderRight>

      </HeaderRight>
    </HeaderWrapper>
  )
}
