import styled from 'styled-components'

export const RightWrapper = styled.div`
  display: flex;
  position: relative;
  top: 4px;
  margin-left: -30px;

  .left {
    display: flex;
    align-items: center;
    margin-right: 6px;

    .l-control {
      display: block;
      width: 25px;
      height: 25px;
      margin-left: 2px;
      text-align: center;
      font-size: 17px;
      color: #9f9f9f;

      &:hover {
        color: #fff;
      }
    }
  }

  .right {
    display: flex;
    align-items: center;
    width: 126px;
    padding-left: 13px;
    background-position: -147px -238px;

    .r-control {
      width: 25px;
      height: 25px;
      margin-right: 2px;
    }

    .volume {
      background-position: -2px -248px;
    }

    .loop {
      background-position: ${props => {
        switch (props.playSequence) {
          case 1:
            return '-66px -248px;'
          case 2:
            return '-66px -344px;'
          default:
            return '-3px -344px;'
        }
      }};
    }

    .playlist {
      /* position: relative; */
      width: 59px;
      padding-left: 18px;
      text-align: center;
      line-height: 26px;
      color: #666;
      background-position: -42px -68px;

      &:hover {
        text-decoration: none;
      }
    }
  }

  .top-volume {
    position: absolute;
    top: -117px;
    left: 67px;
    width: 32px;
    height: 113px;
    overflow: hidden;
    padding: 10px;
    background-position: 0 -503px;

    /* ant design Slider style change */
    .ant-slider-vertical {
      margin: 0;
      .ant-slider-rail {
        background-color: transparent;
      }

      .ant-slider-track {
        background: url(${require('@/assets/img/playbar_sprite.png')}) no-repeat
          0 9999px;
        background-position: -40px bottom;
      }

      .ant-slider-handle {
        border: 0;
        background: url(${require('@/assets/img/sprite_icon.png')});
        background-position: -42px -250px;
      }
    }
  }
`
