import styled from 'styled-components'

export const PlaylistItemWrapper = styled.div`
  display: flex;
  align-items: center;
  padding-left: 15px;
  height: 28px;
  cursor: pointer;
  justify-content: space-around;
  margin-top: 2px;

  &:hover {
    background-color: rgba(0, 0, 0, 0.4);
    color: #fff;
  }

  &.active {
    background-color: rgba(0, 0, 0, 0.4);
    color: #fff;
  }

  .song-name {
    width: 256px;
    height: 28px;
    padding-left: 10px;
    color: #ccc;
    text-align: left;
    line-height: 28px;
  }

  .control-and-singer {
    display: flex;

    .anticon-delete,
    .anticon-download,
    .anticon-folder-add {
      opacity: 0;
      color: #ccc;
      font-size: 14px;
      margin: 2px 6px 0;
    }

    .anticon-delete:hover,
    .anticon-download:hover,
    span:hover,
    .anticon-folder-add:hover {
      color: #fff;
    }

    span {
      margin-left: 4px;
      color: #9b9b9b;
    }
  }

  &:hover .anticon-delete,
  &:hover .anticon-download,
  &:hover .anticon-folder-add {
    opacity: 1;
  }

  .duration {
    color: #666;
  }
`
