import React, {memo, useEffect} from 'react'
import {useDispatch, useSelector, shallowEqual} from 'react-redux'
import {getNewAlbumAsync} from '../../redux/action'

import {NewAlbumWrapper} from './style'
import RecConHeader from '@/components/rec-content-header' 
import {Carousel} from 'antd'

export default memo(function NewAlbum() {

  //组件挂载时发送网络请求获取热门推荐数据
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getNewAlbumAsync())
  }, [dispatch])

  //网络请求成功后，取出保存在redux中的热推数据
  const {newAlbum} = useSelector(state => ({newAlbum: state.getIn(['recommend','newAlbum'])}), shallowEqual)
  // console.log(newAlbum);

  return (
    <NewAlbumWrapper>
      <RecConHeader title="新碟上架"></RecConHeader>
      <div className="new-album-content">
        <div className="inside-wrapper">
          <Carousel>
            {
              [0, 1].map(item => {
                return (
                  <div className="album">
                    {
                      newAlbum.slice(item * 5, (item + 1) * 5).map(obj => {
                        return (
                          <div className="album-cover">{obj.item}</div>
                        )
                      })
                    }
                  </div>
                )
              })
            }
          </Carousel>
        </div>
        <button className="control">1</button>
        <button className="control">2</button>
      </div>
    </NewAlbumWrapper>
  )
})
