import styled from 'styled-components'

export const NewAlbumWrapper = styled.div`
  margin-top: 20px;

  .new-album-content {
    position: relative;
    height: 184px;
    margin: 20px 0;
    padding: 26px 25px 15px;
    border: 1px solid #d3d3d3;
    background-color: #f5f5f5;

    .inside-wrapper {
      height: 100%;

      .banner {
        display: flex;
        width: 100%;
        height: 100%;
        
      }

      .album {
        display: flex;
        width: 100%;
        height: 140px;
        justify-content: space-between;
      }

      .album-cover {
        width: 118px;
        height: 140px;
        background-color: skyblue;
      }

      .control {
        position: absolute;
        width: 20px;
        height: 20px;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        margin: auto;
      }
    }
  }

`
