import React, {memo, useEffect, useRef} from 'react';
import {shallowEqual, useDispatch, useSelector} from 'react-redux';
import propTypes from 'prop-types';
import PlaylistItem from './playlist-item';
import {MusicListWrapper, MusicListHeader, MusicListMain} from './style';
import {ClearOutlined, CloseOutlined} from '@ant-design/icons';
import LyricContent from './lyric-content';

function MusicList(props) {
  // props
  const {showPlaylist} = props;

  const playlistRef = useRef();

  return (
    // 阻止事件冒泡
    <MusicListWrapper style={{ visibility: showPlaylist ? 'visible' : 'hidden' }}
      onClick={(e) => e.stopPropagation()}>
      <MusicListHeader>
        <div className="header-left">
          <h3 className="header-title">播放列表({0})</h3>
          <div>
            <a href="#" className="header-icon">
              <ClearOutlined />
              <span>清除播放列表</span>
            </a>
          </div>
        </div>
        <div className="header-right">
          <div className="song-name">{currentSong.name}</div>
          <div className="close-playlist" onClick={closeWindow}>
            <CloseOutlined />
          </div>
        </div>
      </MusicListHeader>
      <MusicListMain ref={playlistRef}>
        <div className="main-playlist">
          {/* {playList &&
            playList.map((item, index) => {
              return (
                <PlaylistItem
                  key={item.id}
                  isActive={
                    (currentSongIndex ? currentSongIndex : 0) === index
                      ? 'active'
                      : ''
                  }
                  songName={item.name}
                  singer={item.ar[0].name}
                  duration={item.dt}
                  clickItem={() => clickItem(index, item)}
                  songId={item.id}
                  nextMusic={() => changeSong(1)}
                />
              );
            })} */}
        </div>
        <div className="main-line"></div>
        <LyricContent />
      </MusicListMain>
    </MusicListWrapper>
  );
}

// MusicList.propTypes = {
//   isShowSlider: propTypes.bool.isRequired,
//   playlistCount: propTypes.number.isRequired,
// };

export default memo(MusicList);
