import React, {useEffect, useState} from 'react'
import {useDispatch, useSelector, shallowEqual} from 'react-redux'
import {getTopBannerAsync} from '../../redux/action'
import {Carousel} from 'antd'
import {BannerWrapper, BannerLeft, BannerRight} from './style'

export default function Banner() {

  //在组件自身状态中，保存轮播图索引
  const [currentIndex, setCurrentIndex] = useState(0)

  //组件挂载时发送网络请求获取轮播图数据
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getTopBannerAsync())
  }, [])

  //网络请求成功后，将保存在redux中的banner数据取除
  const {banners} = useSelector(state => ({banners: state.getIn(['recommend','topBanner'])}), shallowEqual)

  //
  const bgImage = banners && banners[currentIndex] && banners[currentIndex].imageUrl + '?imageView&blur=40x20'

  return (
    <BannerWrapper bgImage={bgImage}>
      <div className="w980 banner">
        <BannerLeft>
          <Carousel autoplay effect="fade">
            {
              banners && banners.map(obj => {
                return (
                  <div key={obj.imageUrl}>
                    <img src={obj.imageUrl} alt={obj.typeTitle}></img>
                  </div>
                )
              })
            }

          </Carousel>
        </BannerLeft>

        <BannerRight>

        </BannerRight>

      </div>
    </BannerWrapper>
  )
}
