import styled from 'styled-components'

export const MusicListWrapper = styled.div`
  position: absolute;
  left: -425%;
  bottom: 50px;
  width: 986px;
  height: 301px;
  border-radius: 7px;
  z-index: 100;
`
export const MusicListHeader = styled.div`
  display: flex;
  height: 40px;
  background: url(${require('@/assets/img/playlist_bg.png')});
  background-position: 0 0;

  .header-left {
    width: 553px;
    display: flex;
    justify-content: space-between;
    line-height: 40px;
    padding: 0 10px 0 15px;

    .header-title {
      font-size: 14px;
      font-weight: bold;
      color: #e2e2e2;
    }

    .header-icon {
      padding: 5px;
      line-height: 33px;
      color: #ccc;
      span {
        margin-left: 5px;
      }
    }
  }

  .header-right {
    display: flex;
    align-items: center;
    width: 432px;
    color: #fff;
    text-align: center;

    .song-name {
      width: 346px;
      margin-left: 40px;
      line-height: 40px;
      font-size: 14px;
    }

    .close-playlist {
      width: 30px;
      height: 30px;
      font-size: 14px;
      line-height: 30px;
      cursor: pointer;
    }
  }
`

export const MusicListMain = styled.div`
  display: flex;
  height: 260px;
  background: url(${require('@/assets/img/playlist_bg.png')});
  background-position: -1014px 0;
  background-repeat: repeat-y;

  .main-playlist {
    width: 555px;
    padding-left: 2px;
    overflow: auto;

    /* 滚动条 */
    ::-webkit-scrollbar-thumb:horizontal {
      /*水平滚动条的样式*/
      width: 4px;
      background-color: #9f9f9f;
      border-radius: 4px;
    }
    ::-webkit-scrollbar-track-piece {
      background-color: #1a1a1a; /*滚动条的背景颜色*/
      border-radius: 4px;
    }
    ::-webkit-scrollbar {
      width: 8px; /*滚动条的宽度*/
      height: 6px; /*滚动条的高度*/
    }
    ::-webkit-scrollbar-thumb:vertical {
      /*垂直滚动条的样式*/
      height: 50px;
      background-color: #9f9f9f;
      border: 2px solid #9f9f9f;
      border-radius: 4px;
    }

    .active {
      background-color: rgba(0, 0, 0, 0.4);
    }
  }

  .main-line {
    width: 6px;
    height: 258px;
    background: #000;
    margin-top: 2px;
  }

`
