import {GET_TOP_BANNER} from './constants'
import {Map} from 'immutable'

const initState = {
  topBanner: [],
  count: 0
}

export default function recommendReducer(preState = initState, action) {
  console.log('@@@', preState)
  const {type, val} = action
  switch(type) {
    case GET_TOP_BANNER:
      return 1
    case 'increment':
      return preState.set('count', val)
    default:
      // console.log('###')
      return preState
  }
}
