import styled from 'styled-components'

export const PlaylistItemWrapper = styled.div`
  display: flex;
  align-items: center;
  width: 552px;
  height: 28px;
  padding-left: 30px;
  margin-top: 2px;
  cursor: pointer;

  &.active,
  &:hover {
    background-color: rgba(0, 0, 0, 0.4);

    .song-name,
    .singer,
    .duration {
      color: #fff;
    }
  }

  .song-name {
    width: 246px;
    height: 28px;
    line-height: 28px;
    color: #ccc;
  }

  .control {
    display: flex;
    width: 108px;
    padding-left: 10px;

    .icon {
      background: url(${require('@/assets/img/playlist.png')});
      text-indent: -9999px;
    }
  }


  .singer {
    width: 80px;
    padding-left: 10px;
    color: #9b9b9b;
  }

  .duration {
    width: 45px;
    padding-left: 10px;
    margin-left: 10px;
    color: #666;
  }

`
